def encoder(code):
    encoded = {
        "plus_one_letter": "",
        "all_upper_cases": "",
        "all_lower_cases": "",
        "all_numbers": "",
    }

    for char in code:
        encoded["plus_one_letter"] += chr(ord(char) + 3)

        if char.isupper():
            encoded["all_upper_cases"] += char

        if char.islower():
            encoded["all_lower_cases"] += char

        if char.isnumeric():
            encoded["all_numbers"] += char

    return encoded


